import React from "react";
// import { data } from "../helper/data";
import "../App.css";

const PlayerCard = ({ players, searchTerm }) => {
  // console.log(statistics);
  // console.log(searchTerm===name);
  //   console.log(players);
  const playerNames = players.map((player) => player.name);
//   console.log(playerNames);
  const filteredPlayer = playerNames.includes(searchTerm)
    ? players.filter((player) => player.name === searchTerm)
    : players;
  // const filteredPlayer =
  // category === "all"
  //   ? products
  //   : products.filter((product) => product.category === category);

  return (
    <div className="player-card">
      {filteredPlayer.map((item) => (
        <div key={item.name} className="card">
          <img src={item.img} alt="" />
          <p>{item.name}</p>
          {/* <h2>{item.statistics}</h2> */}
        </div>
      ))}
    </div>
  );
};
export default PlayerCard;
