import React, { useState } from "react";
// import { data } from "../helper/data";
import "../App.css";

const PlayerCard = ({ players, searchTerm }) => {
    const filteredPlayers = players.filter((player) =>
    player.name.toLowerCase().includes(searchTerm.toLowerCase())
  ); //!Değişiklikleri anlık olarak almak için filter kullanıyoruz.
  const [selectedPlayerStats, setSelectedPlayerStats] = useState(null);
  const handlePlayerClick = (stats) => {
    setSelectedPlayerStats(stats);
  };
  return (
    <div className="player-card">
      {filteredPlayers.map((item) => (
        <div key={item.name} className="card">
          <img src={item.img} alt={item.name} onClick={() => handlePlayerClick(item.statistics)}/>
          <p>{item.name}</p>
          <ul>
            {item.statistics.map((stat, index) => (
              <li key={index}>{stat}</li>
            ))}
          </ul>
        </div>
      ))}
    </div>
  );
};
export default PlayerCard;
